<?php/** *  Template name: Новости *  *  Template post type: page * * @package Them */get_header(); ?><main>    <?php    $args = array(        'numberposts' => 5,        'category'    => 0,        'orderby'     => 'date',        'order'       => 'DESC',        'include'     => array(),        'exclude'     => array(),        'meta_key'    => '',        'meta_value'  => '',        'post_type'   => 'news',        'suppress_filters' => true, // подавление работы фильтров изменения SQL запроса    );    $loop = new WP_Query($args);    while ($loop->have_posts()) : $loop->the_post(); ?>    <a href="<?php the_permalink(); ?>"><?php the_title(); ?></a>    <?php echo '<div class="entry-content">';        the_content();        echo '</div>';    endwhile; ?></main><?php get_footer();?>