<?php/*Чистка от кода от хуков*/add_filter('show_admin_bar', '__return_false');remove_action('wp_head',             'print_emoji_detection_script', 7);remove_action('admin_print_scripts', 'print_emoji_detection_script');remove_action('wp_print_styles',     'print_emoji_styles');remove_action('admin_print_styles',  'print_emoji_styles');remove_action('wp_head', 'wp_resource_hints', 2); //remove dns-prefetchremove_action('wp_head', 'wp_generator'); //remove meta name="generator"remove_action('wp_head', 'wlwmanifest_link'); //remove wlwmanifestremove_action('wp_head', 'rsd_link'); // remove EditURIremove_action('wp_head', 'rest_output_link_wp_head'); // remove 'https://api.w.org/remove_action('wp_head', 'rel_canonical'); //remove canonicalremove_action('wp_head', 'wp_shortlink_wp_head', 10); //remove shortlinkremove_action('wp_head', 'wp_oembed_add_discovery_links'); //remove alternateif (!defined('_S_VERSION')) {    // Replace the version number of the theme on each release.    define('_S_VERSION', '1.0.0.0');}if (!function_exists('them_setup')) :    function them_setup()    {        load_theme_textdomain('them', get_template_directory() . '/languages');        add_theme_support('automatic-feed-links');        add_theme_support('title-tag');        add_theme_support('post-thumbnails');        // This theme uses wp_nav_menu() in one location.        register_nav_menus(            array(                'menu-1' => esc_html__('Primary', 'them'),            )        );        add_theme_support(            'html5',            array(                'search-form',                'comment-form',                'comment-list',                'gallery',                'caption',                'style',                'script',            )        );        // Set up the WordPress core custom background feature.        add_theme_support(            'custom-background',            apply_filters(                'them_custom_background_args',                array(                    'default-color' => 'ffffff',                    'default-image' => '',                )            )        );        // Add theme support for selective refresh for widgets.        add_theme_support('customize-selective-refresh-widgets');        add_theme_support(            'custom-logo',            array(                'height'      => 250,                'width'       => 250,                'flex-width'  => true,                'flex-height' => true,            )        );    }endif;add_action('after_setup_theme', 'them_setup');/** * Set the content width in pixels, based on the theme's design and stylesheet. * * Priority 0 to make it available to lower priority callbacks. * * @global int $content_width */function them_content_width(){    $GLOBALS['content_width'] = apply_filters('them_content_width', 640);}add_action('after_setup_theme', 'them_content_width', 0);function them_scripts(){    // Тож чистка от ненужных отображений в коде    wp_dequeue_style('wp-block-library');    wp_deregister_script('wp-embed');    // Подключение стилей    wp_enqueue_style('them-style', get_stylesheet_uri(), array(), _S_VERSION);    wp_enqueue_style('google-fonts', 'https://fonts.googleapis.com/css2?family=Roboto:wght@400;500;700&display=swap', [], _S_VERSION);    wp_style_add_data('them-style', 'rtl', 'replace');    wp_enqueue_style( 'bootstrap-style','//cdn.jsdelivr.net/npm/bootstrap@5.0.0-beta3/dist/css/bootstrap.min.css');              // Подключение Скриптов        wp_enqueue_script('them-navigation', get_template_directory_uri() . '/js/navigation.js', array(), _S_VERSION, true);        wp_enqueue_script('them-main', get_template_directory_uri() . '/js/main.js', array(), _S_VERSION, true);       wp_enqueue_script('them-start-script', get_template_directory_uri() . '/js/start-script.js', array(), _S_VERSION, true);        wp_deregister_script( 'bootstrap' );        wp_register_script( 'bootstrap', '//cdn.jsdelivr.net/npm/bootstrap@5.0.0-beta3/dist/js/bootstrap.bundle.min.js');        wp_enqueue_script( 'bootstrap' );        if (is_singular() && comments_open() && get_option('thread_comments')) {        wp_enqueue_script('comment-reply');    }}add_action('wp_enqueue_scripts', 'them_scripts');/** * Implement the Custom Header feature. */require get_template_directory() . '/inc/custom-header.php';/** * Custom template tags for this theme. */require get_template_directory() . '/inc/template-tags.php';/** * Functions which enhance the theme by hooking into WordPress. */require get_template_directory() . '/inc/template-functions.php';/** * Customizer additions. */require get_template_directory() . '/inc/customizer.php';/** * Load Jetpack compatibility file. */if (defined('JETPACK__VERSION')) {    require get_template_directory() . '/inc/jetpack.php';}// Собственный тип записиadd_action('init', 'create_post_type');function create_post_type(){    register_post_type(        'news',        array(            'labels' => array( // добавляем новые элементы в административную частьку                'name' => __('Новости'),                'singular_name' => __('Новости'),                'has_archive' => true,                'add_new' => 'Добавить новую Новость',                'not_found' => 'Ничего не найдено',                'not_found_in_trash' => ''            ),            'hierarchical' => 'true',            'menu_position' => 5, // позиция в меню сразу за запясями            'rewrite' => 'news', //сслыка на сайт            'public' => true,            'has_archive' => true,            'supports' => array( //добавляем элементы в редактор                'title',                'editor',                'author',                'trackbacks',                'thumbnail',                'page-attributes',                'post-formats',                'custom-fields'            ),            'taxonomies' => array('category', 'post_tag') //добавляем к записям необходимый набор таксономий        )    );}